import { useSelector } from "react-redux";
import "./App.css";
import Account from "./components/Account";
import Bonus from "./components/Bonus";
function App() {
  useSelector((state) => state.account.account);
  return (
    <div className="App">
      <h4>App</h4>
      <h3>Current Amount : </h3>
      <h3>Total Bonus : </h3>

      <Account></Account>
      <Bonus></Bonus>
    </div>
  );
}

export default App;
